sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;

	var message ='';
	var date = new Date(); //get the current date.
	var hours = date.getHours(); //get the current hour	
	var time = date.getTime() //get the current time	

	// calculations based on hour to get the correct greeting
	if(hours > 4 && hours <= 12) {
			message = message+ 'Good Morning';
	} else if(hours > 12 && hours <= 16) {
			message = message+ 'Good Afternoon';
	} else if(hours > 16 && hours <= 20) {
			message = message+ 'Good Evening';
	} else if(hours > 20 || hours <= 4) {
			message = message+ 'Good Night';
	}	

  c.data.greet = message; 
	c.data.date = date;
	c.data.time = time;	

	var quote = '';
	var res = JSON.parse(c.data.response);
	//debugger;
  var length = res.length;
  var random = Math.floor(Math.random() * length); 
  c.data.quote = res[random].q;
	c.data.auther = res[random].a;
	console.log(res);

};

