sp_widget.client_script:
api.controller = function() {
    /* widget controller */
    var c = this;


    //c.server.update();
    c.getData = function() {
        document.getElementById("weather-info").style.display = "none" 

        if (c.data.cityName == undefined) {
            alert('Invalid city');
        }

        c.server.get({
            action: "getinfo",
            loc: c.data.cityName
        }).then(function(resp) {
            c.data.result = JSON.parse(resp.data.response);
            console.log(c.data.result);
            displayData(c.data.result);
        })

    }


    displayData = function(myData) {
       // alert(myData.cod);

        if (myData.cod != '404') {
            document.getElementById("weather-info").style.display = "flex" 
            temp = myData.main;
            c.data.currentTemp = Math.round(temp.temp * 10) / 100;
            c.data.minTemp = Math.round(temp.temp_min * 10) / 100;
            c.data.maxTemp = Math.round(temp.temp_max * 10) / 100;

            rain = myData.weather[0];
            c.data.rainStatus = rain.description.toUpperCase();
            c.data.windSpeed = myData.wind.speed;

        } else {
					alert('Invalid Data');
				}
    }




};

