sp_widget.script:
(function() {
    /* populate the 'data' object */
    /* e.g., data.table = $sp.getValue('table'); */

    data.response = "";

    if (input != undefined && input.action == 'getinfo') {
       // gs.addInfoMessage(input.loc);

        try {
            var r = new sn_ws.RESTMessageV2('x_58872_needit.Open Weather Info - AJ', 'Default GET');
            r.setStringParameterNoEscape('API_key', '85393303033c2196f95c435d5940fe86');
            r.setStringParameterNoEscape('City_Name', input.loc);
            var response = r.execute();
            var responseBody = response.getBody();
            var httpStatus = response.getStatusCode();
            data.response = responseBody;
            //		 data.response = responseBody;	 
        } catch (ex) {
            var message = ex.message;
        }

    }

})();
