sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (newValue === '') {
        return;
    }

    var mandatoryField = g_form.getControl('subcategory');
    var isMandatory = g_form.isMandatory('subcategory');

    if (newValue == 'inquiry') {
        if (isMandatory) {
            // mandatoryField.setAttribute('style', 'display:none');
            g_form.setMandatory('subcategory', false);
        }
        g_form.setDisplay('subcategory', false);
    } else {
        if (!isMandatory) {
            //  mandatoryField.setAttribute('style', 'display:block');
            g_form.setMandatory('subcategory', false);
        }
        g_form.setDisplay('subcategory', true);
    }

}
