sys_script_include.script:
var GetRecords_RestAPI_AJ = Class.create();
GetRecords_RestAPI_AJ.prototype = {
    initialize: function() {},

    getList: function(tablename, limit) {

        var myList = [];
        var grList = new GlideRecord(tablename);
        grList.addQuery('active', '1');
        grList.orderBy('number');
        if (limit && limit > 0) {
            grList.setLimit(limit);
        }
        grList.query();

        while (grList.next()) {
            var obj = {};
            obj.sys_id = grList.getValue('sys_id');
            obj.display_value = grList.getDisplayValue();
            myList.push(obj);
        }

        return myList;

    },


    type: 'GetRecords_RestAPI_AJ'
};
