sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

  // Example Query parameters
  // https://<instance_rest_endpoint>?tablename=incident&limit=10
  var qP   = request.queryParams; 
  var tableName = qP.tableName;
  var limit  = qP.limit;


  function getList(tablename,limit){
     var myList = [];
     var grList = new GlideRecord(tablename);
     grList.addQuery('active','1');
     grList.orderBy('number');
     if (limit && limit > 0 ) {
          grList.setLimit(limit);
     } 
     grList.query();

     while (grList.next()){
        var obj = {};
        obj.sys_id = grList.getValue('sys_id');
        obj.display_value = grList.getDisplayValue();
        myList.push(obj);   
     }

     return myList;  

}

var myRecords = getList('incident',10);

response.setBody(myRecords);

})(request, response);
